<?phpuse yii\helpers\Html;use yii\widgets\DetailView;/* @var $this soft\web\View *//* @var $model backend\modules\translationmanager\models\SourceMessage */$this->title = $model->id;$this->params['breadcrumbs'][] = ['label' => 'Translations', 'url' => ['index']];$this->params['breadcrumbs'][] = $this->title;?><?php if (!$this->isAjax): ?><?= Html::a('Редактировать', ['update', 'id' => $model->id], ['class' => 'btn btn-primary']) ?><?= Html::a('Удалить', ['delete', 'id' => $model->id], [    'class' => 'btn btn-danger',    'data' => [        'confirm' => 'Вы уверены, что хотите безвозвратно удалить Translation?',        'method' => 'post',    ],]) ?><?php endif ?><?php$arr = [    'id',    'category',    'message:ntext',];foreach ($model->languages as $key => $one) {    $arr[] = ['label' => $key, 'value' => $one];}?><?= DetailView::widget([    'model' => $model,    'attributes' => $arr,]) ?>