<?phpnamespace soft\extra;use yii\base\Widget;/** * Render items based on given template */class TemplateRender extends Widget{    /**     * @var string template is the template of the items     * it can be like: "{item1_name} {item2_name}"     * if value is `-1` all items will be rendered     */    public $template = -1;    /**     * @var array items `name => value` pairs     */    public $items = [];    /**     * @var string separator between items, this only available if template=-1     */    public $separator = '';    public function run()    {        return $this->renderItems();    }    public function renderItems()    {        if ($this->template == false || empty($this->template)) {            return false;        } elseif ($this->template === -1) {            $result = "";            foreach ($this->items as $key => $value) {                $result .= $value . $this->separator;            }            return $result;        } else {            $result = $this->template;            foreach ($this->items as $key => $value) {                $result = strtr($result, [                    "{{$key}}" => $value                ]);            }            return $result;        }    }}?>