<?phpnamespace soft\extra;use soft\helpers\ArrayHelper;/** * Render toggle data (tooltip or popover) for html elements */class Toggle{    const TYPE_TOOLTIP = 'tooltip';    const TYPE_POPOVER = 'popover';    const PLACEMENT_TOP = 'top';    const PLACEMENT_LEFT = 'left';    const PLACEMENT_BOTTOM = 'bottom';    const PLACEMENT_RIGHT = 'right';    const TRIGGER_CLICK = 'click';    const TRIGGER_FOCUS = 'focus';    const TRIGGER_HOVER = 'hover';    const DEFAULT_CONFIGS = [        'toggle' => self::TYPE_TOOLTIP,        'placement' => self::PLACEMENT_BOTTOM,        'trigger' => self::TRIGGER_HOVER,    ];    /**     * Add toggle data to options     * @param $options array     * @param $toggle     * @param $configs     * @return array     */    public static function addToggleOptions($options, $toggle, $configs)    {        $configs = ArrayHelper::merge(self::DEFAULT_CONFIGS, $configs);        $configs['toggle'] = $toggle;        foreach ($configs as $key => $value) {            $options['data-' . $key] = $value;        }        return $options;    }}?>