<?phpuse yii\helpers\ArrayHelper;function not_found($message = null){    if ($message == null) {        $message = t('Page not found.', 'yii');    }    throw new \yii\web\NotFoundHttpException($message);}function forbidden($message = null){    if ($message == null) {        $message = t('You are not allowed to perform this action!', 'yii');    }    throw new \yii\web\ForbiddenHttpException($message);}/** * Check if bs version is 4 * @return boolean * @throws Exception */function isBs4(){    $v = ArrayHelper::getValue(Yii::$app->params, 'bsVersion', '3');    $ver = (string)$v;    return substr(trim($ver), 0, 1) == '4';}function redirect($url, $statusCode = 302){    return Yii::$app->controller->redirect($url, $statusCode);}