<?phpnamespace soft\web;use Yii;use common\models\User;use yii\base\Model;use yii\db\ActiveRecord;use yii\web\Request;use yii\web\Response;use yii\web\Session;/** * SoftController va View uchun qo'shimcha metodlar * * @property-read bool $hasUser Whether the current user is a not guest * @property-read User $user * @property-read Session $session **/trait VCTrait{    # <editor-fold defaultstate="collapsed" desc="Model">    /**     * @param $model ActiveRecord     * @return mixed     */    public function modelLoad(&$model)    {        return $model->load(Yii::$app->request->post());    }    /**     * @param $model ActiveRecord     * @return mixed     */    public function modelSave(&$model)    {        return $model->load(Yii::$app->request->post()) && $model->save();    }    /**     * @param $model \yii\base\Model     * @return bool     */    public function modelValidate(Model &$model)    {        return $model->load(Yii::$app->request->post()) && $model->validate();    }    # </editor-fold>    # <editor-fold defaultstate="collapsed" desc="User">    /**     * @return \yii\web\User     */    public function getUser()    {        return Yii::$app->user;    }    /**     * @return bool     */    public function getHasUser()    {        return !(Yii::$app->user->isGuest);    }    # </editor-fold>    # <editor-fold defaultstate="collapsed" desc="Session">    /**     * @return Session     */    public function getSession()    {        return Yii::$app->session;    }    /**     * @param $key     * @param bool $value     * @param bool $removeAfterAccess     * @see Session::setFlash()     */    public function setFlash($key, $value = true, $removeAfterAccess = true)    {        $this->getSession()->setFlash($key, $value, $removeAfterAccess);    }    /**     * @param $key     * @param null $defaultValue     * @param false $delete     * @see Session::getFlash()     * @return mixed     */    public function getFlash($key, $defaultValue = null, $delete = false)    {        return $this->getSession()->getFlash($key, $defaultValue, $delete);    }    # </editor-fold>}