<?phpnamespace soft\widget\kartik;use Exception;use yeesoft\multilingual\containers\MultilingualFieldContainer;use yeesoft\multilingual\widgets\FormLanguageSwitcher;use yii\base\Model;class ActiveForm extends \kartik\widgets\ActiveForm{    public $fieldClass = 'soft\widget\kartik\ActiveField';    /**     * Renders form language switcher.     *     * @param Model $model     * @param string $view     * @return string     * @throws Exception     */    public function languageSwitcher($model, $view = null)    {        $languages = ($model->getBehavior('multilingual')) ? $languages = $model->getBehavior('multilingual')->languages : [];        return FormLanguageSwitcher::widget(['languages' => $languages, 'view' => $view])."<br>";    }    public function field($model, $attribute, $options = [])    {        $fields = [];        $isNonMultilingual = (isset($options['multilingual']) && $options['multilingual'] === false);        $isFieldMultilingual = (isset($options['multilingual']) && $options['multilingual']);        $isAttributeMultilingual = ($model->getBehavior('multilingual') && $model->hasMultilingualAttribute($attribute));        if (!$isNonMultilingual && ($isFieldMultilingual || $isAttributeMultilingual)) {            $languages = array_keys($model->getBehavior('multilingual')->languages);            foreach ($languages as $language) {                $fields[] = parent::field($model, $attribute, array_merge($options, ['language' => $language]));            }        } else {            return parent::field($model, $attribute, $options);        }        return new MultilingualFieldContainer(['fields' => $fields]);    }}